#! /usr/bin/env node
const { exec } = require('child_process')
exec(
  './node_modules/.bin/electron ./src/backend',
  { env: { NODE_ENV: 'prduction' } },
  (error, stdout, stderr) => {
    if (error) {
      console.error(error.message)
    }
    console.log(`stdout: ${stdout}`)

    console.log(`stderr: ${stderr}`)
  }
)
