export type App = {
  name: string
  port: string
  messages: AppMessage[]
}

export type Apps = {
  [port: string]: App
}

export type AppMessage = {
  type: string
  data: object
}

export type Message = {
  port: string
  message: AppMessage[]
}

export type State = {
  isConnecting: boolean
  isLoading: boolean
  error: string
  currentPort: string
  apps: Apps
  newPortValue: string
}

const state: State = {
  isConnecting: true,
  isLoading: true,
  error: null,
  currentPort: null,
  apps: {},
  newPortValue: '',
}

export default state
