import * as React from 'react'
import { connect, Connect } from '../../app'
import { Wrapper, Port } from './elements'

const PortsBar: React.SFC<Connect> = ({ appState, actions }) => (
  <Wrapper>
    {Object.keys(appState.apps).map((port) => (
      <Port
        key={port}
        onClick={() => actions.openApp(port)}
        isSelected={appState.currentPort === port}
      >
        {port}
      </Port>
    ))}
  </Wrapper>
)

export default connect(PortsBar)
