import * as React from 'react'
import { ButtonElement } from './elements'

type Props = {
  primary: boolean
  onClick: () => void
  disabled?: boolean
}

const Button: React.SFC<Props> = ({ disabled, primary, onClick, children }) => (
  <ButtonElement
    disabled={disabled}
    primary={primary}
    onClick={() => onClick()}
  >
    {children}
  </ButtonElement>
)

export default Button
