import { h } from 'overmind-components'
import * as styles from './styles'
import { Component } from '../../app'

type Props = {
  borderColor?: string
  visible?: boolean
  showPathTag?: boolean
}

const ActionPath: Component<Props> = ({
  showPathTag = true,
  visible,
  borderColor,
  children,
}) => (
  <div className={styles.path(visible)}>
    <div className={styles.pathLine(borderColor)} />
    {children ? (
      <div className={styles.pathText(borderColor, visible && showPathTag)}>
        {children}
      </div>
    ) : null}
  </div>
)

export default ActionPath
