import * as React from 'react'
import { Path, PathLine, PathText } from './elements'
import { Colors } from 'overmind-themes'

type Props = {
  borderColor?: keyof Colors
  visible?: boolean
  showPathTag?: boolean
}

const ActionPath: React.SFC<Props> = ({
  showPathTag = true,
  visible,
  borderColor,
  children,
}) => (
  <Path visible={visible}>
    <PathLine borderColor={borderColor} />
    {children ? (
      <PathText visible={visible && showPathTag} borderColor={borderColor}>
        {children}
      </PathText>
    ) : null}
  </Path>
)

export default ActionPath
