import { css } from 'emotion'

export const path = (visible: boolean) => css`
  position: relative;
  height: ${visible ? '100%' : '0'}
  visibility: ${visible === false ? 'hidden' : 'visible'};
`

export const pathLine = (borderColor?: string) => css`
  position: absolute;
  top: 50%;
  left: 0;
  z-index: 0;
  width: 100%;
  border-bottom: 1px solid ${borderColor || 'var(--color-black-1'};
`

export const pathText = (borderColor: string, visible: boolean) => css`
  position: relative;
  z-index: 1;
  margin: 0 var(--padding-2) 0 0;
  padding: var(--padding-1) var(--padding-2);
  border: 1px solid ${borderColor};
  background-color: #fff;
  border-radius: var(--border-radius-1);
  visibility: ${visible ? 'visible' : 'hidden'};
`
