import * as React from 'react'
import { connect, Connect } from '../../app'
import { Wrapper, App } from './elements'

const AppsBar: React.SFC<Connect> = ({ app }) => (
  <Wrapper>
    {Object.keys(app.state.apps).map((appName) => (
      <App
        key={appName}
        onClick={() => app.actions.selectApp(appName)}
        isSelected={app.state.currentAppName === appName}
      >
        {appName}
      </App>
    ))}
  </Wrapper>
)

export default connect(AppsBar)
