import App, { TDerive, TConfig, TAction } from './'

type State = {
  foo: string
  upperFoo: string
}

describe('Derived', () => {
  test('should instantiate app with derived state', () => {
    const derived: Derive<string> = (state) => state.foo.toUpperCase()
    const state = {
      foo: 'bar',
      upperFoo: derived,
    }
    const config = {
      state,
    }

    type Config = TConfig<{
      state: typeof state
    }>

    type Derive<T> = TDerive<T, Config>

    const app = new App(config)

    expect(app.state.upperFoo).toEqual('BAR')
  })

  test('should track derived state', () => {
    let renderCount = 0
    const changeFoo: Action = ({ mutate }) =>
      mutate((state) => (state.foo = 'bar2'))

    const derived: Derive<string> = (state: State) => state.foo.toUpperCase()

    const config = {
      state: {
        foo: 'bar',
        upperFoo: derived,
      },
      actions: {
        changeFoo,
      },
    }
    type Config = TConfig<{
      state: {
        foo: string
        upperFoo: string
      }
      actions: typeof config.actions
    }>
    type Action<Input = void, Output = any> = TAction<Input, Output, Config>
    type Derive<T> = TDerive<T, Config>

    const app = new App(config)
    function render() {
      const trackId = app.trackState()
      app.state.upperFoo
      return app.clearTrackState(trackId)
    }
    app.addMutationListener(render(), () => {
      renderCount++
    })
    app.actions.changeFoo()
    expect(app.state.upperFoo).toBe('BAR2')
    expect(renderCount).toBe(1)
  })
})
