    const foo = {
      state: {
        foo: 'bar',
      },
      actions: {
        foo: fooAction,
      },
    }
    const bar = {
      state: {
        bar: 'baz',
      },
      effects: {
        hello: () => 'hello',
      },
      actions: {
        bar: fooAction,
      },
    }

    type Config = TConfig<{
      namespaces: {
        foo: typeof foo
        bar: typeof bar
      }
    }>

    type Action<Value = void> = TAction<Value, Config>

