describe('OPERATORS', () => {
  test('fork', () => {
    expect.assertions(2)
    let calledFoo = false
    const doThis: Action = (action) =>
      action().fork(() => 'foo', {
        foo: action()
          .do(() => (calledFoo = true))
          .map(() => '123'),
        bar: action().map(() => 123),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>

    const app = new App(config)

    expect(app.actions.doThis()).toBe(undefined)
    expect(calledFoo).toBe(true)
  })
  test('mutate', () => {
    const doThis: Action = (action) =>
      action().mutation((state) => (state.foo = 'bar2'))
    const config = {
      state: {
        foo: 'bar',
      },
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      state: typeof config.state
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>

    const app = new App(config)

    expect(app.state.foo).toEqual('bar')
    app.actions.doThis()
    expect(app.state.foo).toEqual('bar2')
  })
  test('do', () => {
    expect.assertions(2)
    const doThis: Action<string> = (action) =>
      action<string>().do(({ foo }) => {
        expect(foo.bar()).toBe('baz')
      })
    const config = {
      state: {
        foo: 'bar',
      },
      effects: {
        foo: {
          bar: () => 'baz',
        },
      },
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      state: typeof config.state
      effects: typeof config.effects
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>

    const app = new App(config)
    expect(app.actions.doThis('foo')).toBe('foo')
  })
  test('map', () => {
    expect.assertions(1)
    const doThis: Action<string> = (action) =>
      action<string>().map((_, value) => {
        return value.toUpperCase()
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>

    const app = new App(config)

    expect(app.actions.doThis('foo')).toBe('FOO')
  })

  test('try - resolved', () => {
    expect.assertions(1)
    const doThis: Action = (action) =>
      action().try(() => Promise.resolve(), {
        success: action().map(() => 'foo'),
        error: action().map(() => 'error'),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>
    const app = new App(config)

    return Promise.resolve(app.actions.doThis()).then((value) => {
      expect(value).toBe('foo')
    })
  })

  test('try - rejected', () => {
    expect.assertions(1)
    const doThis: Action = (action) =>
      action().try(() => Promise.reject(new Error()), {
        success: action().map(() => 'foo'),
        error: action().map(() => 'bar'),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>
    const app = new App(config)
    return Promise.resolve(app.actions.doThis()).then((value) => {
      expect(value).toBe('bar')
    })
  })
  test('when - true', () => {
    expect.assertions(1)
    const doThis: Action = (action) =>
      action().when(() => true, {
        true: action().map(() => 'foo'),
        false: action(),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>
    const app = new App(config)
    expect(app.actions.doThis()).toBe('foo')
  })

  test('when - false', () => {
    expect.assertions(1)
    const doThis: Action = (action) =>
      action().when(() => false, {
        true: action().map(() => 'foo'),
        false: action().map(() => 'bar'),
      })
    const config = {
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>
    const app = new App(config)

    expect(app.actions.doThis()).toBe('bar')
  })

  test('filter - true', () => {
    expect.assertions(1)
    const doThis: Action<string> = (action) =>
      action<string>()
        .filter(() => true)
        .map(() => 'bar')
    const config = {
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>
    const app = new App(config)
    expect(app.actions.doThis('foo')).toBe('bar')
  })
  test('filter - false', () => {
    expect.assertions(1)
    const doThis: Action<string> = (action) =>
      action<string>()
        .filter(() => false)
        .map(() => 'bar')
    const config = {
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>
    const app = new App(config)

    expect(app.actions.doThis('foo')).toEqual({ value: 'foo' })
  })
  test('debounce', () => {
    expect.assertions(2)
    const start = Date.now()
    let end
    const doThis: Action = (action) =>
      action()
        .debounce(100)
        .do(() => {
          end = Date.now()
        })
        .map(() => 'foo')
    const config = {
      actions: {
        doThis,
      },
    }
    type Config = TConfig<{
      actions: typeof config.actions
    }>
    type Action<Value = void> = TAction<Value, Config>
    const app = new App(config)

    return Promise.resolve(app.actions.doThis()).then((value) => {
      expect(value).toBe('foo')
      expect(end - start).toBeGreaterThanOrEqual(100)
    })
  })
})
