import { ActionChain } from 'action-chain'
import { EventEmitter } from 'betsy'
import ProxyStateTree from 'proxy-state-tree'
import Devtools, { Message, safeValue } from './Devtools'
import ActionClass, { IValueAction, INoValueAction, Compose } from './Action'
import Reaction from './reaction'
export { default as derive } from './derived'
export { default as compute } from './computed'

export { IValueAction, Compose }

export const log = (...objects: any[]) =>
  console.log(...objects.map((obj) => JSON.parse(JSON.stringify(obj))))

export const dynamicModule = ((cb) => (namespace) =>
  cb(namespace)) as DynamicModule

/*
  BASE TYPES
*/
type DynamicModule = <
  T extends {
    onInitialize?: any
    state?: any
    effects?: any
    actions?: any
    reactions?: any
  }
>(
  cb: (namespace: string) => T
) => ReturnType<typeof cb>

export type Configuration = {
  onInitialize?: any
  state?: any
  effects?: any
  actions?: any
  reactions?: any
  modules?: {
    [namespace: string]: {
      onInitialize?: any
      state?: any
      effects?: any
      actions?: any
      reactions?: any
    }
  }
}

type SubType<Base, Condition> = Pick<
  Base,
  { [Key in keyof Base]: Base[Key] extends Condition ? Key : never }[keyof Base]
>

/*
  DECLARE MODULE TYPES
*/
export interface IState {}

export interface IEffects {}

interface IApp extends IEffects {
  state: IState
}

export type TState<Config extends Configuration> = [Config['state']] extends [
  undefined
]
  ? {
      [P in keyof SubType<Config['modules'], { state: {} }>]: SubType<
        Config['modules'],
        { state: {} }
      >[P]['state']
    }
  : Config['state'] &
      {
        [P in keyof SubType<Config['modules'], { state: {} }>]: SubType<
          Config['modules'],
          { state: {} }
        >[P]['state']
      }

export type TEffects<Config extends Configuration> = [
  Config['effects']
] extends [undefined]
  ? {
      [P in keyof SubType<Config['modules'], { effects: object }>]: SubType<
        Config['modules'],
        { effects: object }
      >[P]['effects']
    }
  : Config['effects'] &
      {
        [P in keyof SubType<Config['modules'], { effects: object }>]: SubType<
          Config['modules'],
          { effects: object }
        >[P]['effects']
      }

export type Mutate<Value = any> = (state: IApp['state'], value: Value) => void

export namespace Operation {
  export type Map<Value, ReturnValue> = (
    effects: IApp,
    value: Value
  ) => ReturnValue
  export type Filter<Value = any> = (effects: IApp, value: Value) => boolean
  export type When<Value = any> = (effects: IApp, value: Value) => boolean
  export type Do<Value = any> = (effects: IApp, value: Value) => void
  export type Fork<Value = any> = (effects: IApp, value: Value) => string
  export type Try<Value, ReturnValue> = (
    effects: IApp,
    value: Value
  ) => ReturnValue
}

export type Action<InitialValue = void, ReturnValue = any> = Compose<
  IApp['state'],
  IApp,
  InitialValue,
  ReturnValue
>

export type DynamicAction<InitialValue = void, ReturnValue = any> = Compose<
  any,
  any,
  InitialValue,
  ReturnValue
>

export type Reaction = (
  reaction: (
    getState: (state: IApp['state']) => any,
    action: TActionCaller<void, IApp>
  ) => any,
  action: TActionCreator<IApp>
) => any

export type Derive<T> = (state: IApp['state']) => T

export type Compute<Input, Output> = (
  value: Input
) => (state: IApp['state']) => Output

/*
  EXPLICIT TYPES (multiple apps), might be DEPRECATED
*/
export type TConfig<Config extends Configuration> = {
  state: [Config['state']] extends [undefined]
    ? {
        [P in keyof SubType<Config['modules'], { state: {} }>]: SubType<
          Config['modules'],
          { state: {} }
        >[P]['state']
      }
    : [Config['modules']] extends [undefined]
      ? Config['state']
      : Config['state'] &
          {
            [P in keyof SubType<Config['modules'], { state: {} }>]: SubType<
              Config['modules'],
              { state: {} }
            >[P]['state']
          }
  effects: [Config['effects']] extends [undefined]
    ? {
        [P in keyof SubType<Config['modules'], { effects: object }>]: SubType<
          Config['modules'],
          { effects: object }
        >[P]['effects']
      }
    : [Config['modules']] extends [undefined]
      ? Config['effects']
      : Config['effects'] &
          {
            [P in keyof SubType<
              Config['modules'],
              { effects: object }
            >]: SubType<Config['modules'], { effects: object }>[P]['effects']
          }
  actions: [Config['actions']] extends [undefined]
    ? {
        [P in keyof SubType<Config['modules'], { actions: object }>]: SubType<
          Config['modules'],
          { actions: object }
        >[P]['actions']
      }
    : [Config['modules']] extends [undefined]
      ? Config['actions']
      : Config['actions'] &
          {
            [P in keyof SubType<
              Config['modules'],
              { actions: object }
            >]: SubType<Config['modules'], { actions: object }>[P]['actions']
          }
  reactions: any
  namespaces: any
}

export type ActionCallback<App> = (action: TActionCreator<App>) => any

export type TAction<
  InitialValue,
  ReturnValue,
  App extends { state: any; effects: any }
> = Compose<App['state'], App['effects'], InitialValue, ReturnValue>

export type TActionCreator<App> = {
  <InitialValue = void>(): TActionCaller<InitialValue, App>
}

export type TActionCaller<InitialValue, App> = [InitialValue] extends [void]
  ? INoValueAction<TConfig<App>['state'], App, InitialValue>
  : IValueAction<TConfig<App>['state'], App, InitialValue>

export namespace TOperation {
  export type Mutate<Value, App> = (
    state: TConfig<App>['state'],
    value: Value
  ) => void
  export type Map<Value, ReturnValue, App> = (
    effects: TConfig<App>['effects'] & { state: TConfig<App>['state'] },
    value: Value
  ) => ReturnValue
  export type Filter<Value, App> = (
    effects: TConfig<App>['effects'] & { state: TConfig<App>['state'] },
    value: Value
  ) => boolean
  export type When<Value, App> = (
    effects: TConfig<App>['effects'] & { state: TConfig<App>['state'] },
    value: Value
  ) => boolean
  export type Do<Value, App> = (
    effects: TConfig<App>['effects'] & { state: TConfig<App>['state'] },
    value: Value
  ) => void
  export type Fork<Value, App> = (
    effects: TConfig<App>['effects'] & { state: TConfig<App>['state'] },
    value: Value
  ) => string
  export type Try<Value, ReturnValue, App> = (
    effects: TConfig<App>['effects'] & { state: TConfig<App>['state'] },
    value: Value
  ) => ReturnValue
}

export type TReaction<App> = (
  reaction: (
    getState: (state: TConfig<App>['state']) => any,
    action: TActionCaller<void, App>
  ) => any,
  action: TActionCreator<App>
) => any

export type TDerive<App> = (state: TConfig<App>['state']) => any

export type TCompute<Value, App> = (
  value: Value
) => (state: TConfig<App>['state']) => any

/*
  CLASS TYPES
*/

type Options = {
  devtools?: string
}

type CacheMessage = {
  value: any
  paths: string[]
  updateCount: number
}

export enum EventType {
  DERIVED = 'derived',
  DERIVED_DIRTY = 'derived:dirty',
  COMPUTED = 'computed',
  COMPUTED_DIRTY = 'computed:dirty',
  REACTION_ADD = 'reaction:add',
  REACTION_UPDATE = 'reaction:update',
  REACTION_REMOVE = 'reaction:remove',
  COMPONENT_ADD = 'component:add',
  COMPONENT_UPDATE = 'component:update',
  COMPONENT_REMOVE = 'component:remove',
}

export interface Events {
  [EventType.DERIVED]: {
    path: string
    paths: string[]
    updateCount: number
    value: any
  }
  [EventType.DERIVED_DIRTY]: {
    path: string
    flushId: number
  }
  [EventType.COMPUTED]: {
    path: string
    updateCount: number
    cache: CacheMessage[]
    limit: number
  }
  [EventType.COMPUTED_DIRTY]: {
    path: string
    flushId: number
  }
  [EventType.REACTION_ADD]: {
    path: string
    statePath: string
    updateCount: number
  }
  [EventType.REACTION_UPDATE]: {
    path: string
    statePath: string
    updateCount: number
  }
  [EventType.REACTION_REMOVE]: {
    path: string
    statePath: string
    updateCount: number
  }
  [EventType.COMPONENT_ADD]: {
    componentId: number
    componentInstanceId: number
    name: string
    paths: string[]
  }
  [EventType.COMPONENT_UPDATE]: {
    componentId: number
    componentInstanceId: number
    name: string
    paths: string[]
    flushId?: number
  }
  [EventType.COMPONENT_REMOVE]: {
    componentId: number
    componentInstanceId: number
    name: string
  }
}

export default class App<
  Config extends Configuration,
  EvalConfig extends TConfig<Config>
> {
  private proxyStateTree: ProxyStateTree
  eventHub: EventEmitter<Events>
  devtools: Devtools
  actions: {
    [T in keyof EvalConfig['actions']]: EvalConfig['actions'][T] extends Function
      ? ReturnType<EvalConfig['actions'][T]>
      : {
          [P in keyof EvalConfig['actions'][T]]: EvalConfig['actions'][T][P] extends Function
            ? ReturnType<EvalConfig['actions'][T][P]>
            : undefined
        }
  }
  state: EvalConfig['state']
  constructor(configuration: Config, options: Options = {}) {
    /*
      Mutate module functions into module objects
    */
    this.mutateModuleFunctionsIntoModules(configuration)
    /*
      Set up an eventHub to trigger information from derived, computed and reactions
    */
    const eventHub = new EventEmitter<Events>()

    /*
      Create the proxy state tree instance with the state and a wrapper to expose
      the eventHub
    */
    const proxyStateTree = new ProxyStateTree(this.getState(configuration), {
      dynamicWrapper: (proxyStateTree, path, func) =>
        func(eventHub, proxyStateTree, path),
    })

    /*
      The action chain with the context configuration
    */
    const actionChain = new ActionChain(
      Object.assign({ state: proxyStateTree.get() }, configuration.effects),
      { providerExceptions: ['state'] }
    )

    /*
      The action factory function
    */
    const action = function<InitialValue>(): [InitialValue] extends [void]
      ? INoValueAction<
          TConfig<Config>['state'],
          TConfig<Config>['effects'] & { state: TConfig<Config>['state'] },
          InitialValue
        >
      : IValueAction<
          TConfig<Config>['state'],
          TConfig<Config>['effects'] & { state: TConfig<Config>['state'] },
          InitialValue
        > {
      return new ActionClass(proxyStateTree, actionChain) as any
    }

    if (options.devtools && typeof window !== 'undefined') {
      this.initializeDevtools(
        options.devtools,
        actionChain,
        eventHub,
        proxyStateTree
      )
    }

    this.initializeReactions(configuration, eventHub, proxyStateTree, action)

    /*
      Identify when the state tree should flush out changes

      TODO:
        - How should I know when an operator is connected to a flushId?
        - Pass in operatorId on :async and set flushId directly?
        - Store operatorId on the flush and check?
    */
    actionChain.on('operator:async', (data) => {
      const flushData = proxyStateTree.flush()
      if (this.devtools && flushData.mutations.length) {
        this.devtools.send({
          type: 'flush',
          data: {
            ...data,
            ...flushData,
          },
        })
      }
    })
    actionChain.on('action:end', (data) => {
      const flushData = proxyStateTree.flush()
      if (this.devtools && flushData.mutations.length) {
        this.devtools.send({
          type: 'flush',
          data: {
            ...data,
            ...flushData,
          },
        })
      }
    })

    /*
      Expose the created actions
    */
    this.actions = this.getActions(configuration, action)

    this.state = proxyStateTree.get()
    this.proxyStateTree = proxyStateTree
    this.eventHub = eventHub

    const initializers = this.getInitializers(configuration)

    if (!initializers.length) {
      return
    }

    const rootInitializer =
      initializers[0].name === 'onInitialize' ? initializers.shift() : null

    let onInitialize = action()

    if (initializers.length) {
      onInitialize = onInitialize.parallel(initializers)
    }

    if (rootInitializer) {
      onInitialize = onInitialize.compose(rootInitializer)
    }

    // @ts-ignore
    onInitialize.displayName = 'onInitialize'
    onInitialize(undefined)
  }
  private mutateModuleFunctionsIntoModules(config: Configuration) {
    if (config.modules) {
      Object.keys(config.modules).forEach((key) => {
        if (typeof config.modules[key] === 'function') {
          config.modules[key] = (config.modules[key] as any)(key)
        }
      })
    }
  }
  private initializeDevtools(host, actionChain, eventHub, proxyStateTree) {
    const devtools = new Devtools()
    devtools.connect(
      host,
      (message: Message) => {
        // To use for communication from devtools app
      }
    )
    actionChain.on('action:start', (data) =>
      devtools.send({
        type: 'action:start',
        data: {
          ...data,
          value: safeValue(data.value),
        },
      })
    )
    actionChain.on('operator:start', (data) =>
      devtools.send({
        type: 'operator:start',
        data,
      })
    )
    actionChain.on('operator:end', (data) =>
      devtools.send({
        type: 'operator:end',
        data: {
          ...data,
          result: safeValue(data.result),
        },
      })
    )
    actionChain.on('action:end', (data) =>
      devtools.send({
        type: 'action:end',
        data,
      })
    )
    actionChain.on('effect', (data) =>
      devtools.send({
        type: 'effect',
        data,
      })
    )
    actionChain.on('mutations', (data) =>
      devtools.send({
        type: 'mutations',
        data,
      })
    )
    for (let type in EventType) {
      eventHub.on(EventType[type], (data) =>
        devtools.send({
          type: EventType[type],
          data,
        })
      )
    }
    // This message is always the first as it is passed synchronously, all other
    // events are emitted async
    devtools.send({
      type: 'init',
      data: {
        state: proxyStateTree.get(),
      },
    })
    this.devtools = devtools
  }
  private initializeReactions(configuration, eventHub, proxyStateTree, action) {
    const reactions = Object.keys(configuration.reactions || {}).reduce(
      (aggr, name) => {
        if (typeof configuration.reactions[name] === 'function') {
          return Object.assign(aggr, {
            [name]: configuration.reactions[name](
              (stateCb, action) => [stateCb, action],
              action
            ),
          })
        }

        return Object.keys(configuration.reactions[name] || {}).reduce(
          (aggr, subName) =>
            Object.assign(aggr, {
              [name + '.' + subName]: configuration.reactions[name](
                (stateCb, action) => [stateCb, action],
                action
              ),
            }),
          {}
        )
      },
      {}
    )
    Object.keys(reactions).forEach((name) => {
      const reaction = new Reaction(eventHub, proxyStateTree, name)
      reaction.create(reactions[name][0], reactions[name][1])
    })
  }
  private getState(configuration) {
    let state = {}
    if (configuration.state) {
      state = this.transformStateToPlainObject(configuration.state)
    }
    if (configuration.modules) {
      Object.keys(configuration.modules).reduce((aggr, key) => {
        if (configuration.modules[key].state) {
          return Object.assign(aggr, {
            [key]: this.transformStateToPlainObject(
              configuration.modules[key].state
            ),
          })
        }

        return aggr
      }, state)
    }

    return state
  }
  private getInitializers(configuration) {
    let initializers = []
    if (configuration.onInitialize) {
      initializers.push(configuration.onInitialize)
    }
    if (configuration.modules) {
      initializers = Object.keys(configuration.modules).reduce((aggr, key) => {
        if (configuration.modules[key].onInitialize) {
          configuration.modules[key].onInitialize.displayName =
            key + '.onInitialize'
          return aggr.concat(configuration.modules[key].onInitialize)
        }

        return aggr
      }, initializers)
    }

    return initializers
  }
  private transformStateToPlainObject(state: {}) {
    return Object.keys(state).reduce((aggr, key) => {
      aggr[key] = state[key]

      return aggr
    }, {})
  }
  private getActions(configuration, action) {
    let actions = {}
    if (configuration.actions) {
      actions = configuration.actions
    }
    if (configuration.modules) {
      Object.keys(configuration.modules).reduce((aggr, key) => {
        if (configuration.modules[key].actions) {
          return Object.assign(aggr, {
            [key]: configuration.modules[key].actions,
          })
        }

        return aggr
      }, actions)
    }

    const evaluatedActions = Object.keys(actions).reduce((aggr, name) => {
      if (typeof actions[name] === 'function') {
        return Object.assign(aggr, {
          [name]: actions[name](action),
        })
      }

      return Object.assign(aggr, {
        [name]: Object.keys(actions[name] || {}).reduce(
          (aggr, subName) =>
            Object.assign(aggr, {
              [subName]: actions[name][subName](action),
            }),
          {}
        ),
      })
    }, {}) as any

    if (this.devtools) {
      Object.keys(evaluatedActions).forEach((key) => {
        if (typeof evaluatedActions[key] === 'function') {
          evaluatedActions[key].displayName = key
        } else {
          Object.keys(evaluatedActions[key]).forEach((subKey) => {
            evaluatedActions[key][subKey].displayName = key + '.' + subKey
          })
        }
      })
    }

    return evaluatedActions
  }
  trackState() {
    return this.proxyStateTree.startPathsTracking()
  }
  clearTrackState(id: number) {
    return this.proxyStateTree.clearPathsTracking(id)
  }
  addMutationListener(paths, cb) {
    return this.proxyStateTree.addMutationListener(paths, cb)
  }
  createReactionFactory(prefix: string) {
    const reactions = []
    const instance = this
    return {
      add(
        name: string,
        stateCb: (state: TConfig<Config>['state']) => any,
        cb: Function
      ) {
        const reaction = new Reaction(
          instance.eventHub,
          instance.proxyStateTree,
          prefix + '.' + name
        )
        reaction.create(stateCb, cb)

        reactions.push(reaction)
      },
      dispose() {
        reactions.forEach((reaction) => reaction.destroy())
        reactions.length = 0
      },
    }
  }
}
